-- DESCRIPTION: Handles all actions on player speedlocal gameplayerspeed = {}function gameplayerspeed.startgame()endfunction gameplayerspeed.movingspeed(baserunningspeed) if g_UserGlobal ~= nil then  local user_defined_global_max = "MyStaminaMaximum"  if user_defined_global_max > "" then    if _G["g_UserGlobal['"..user_defined_global_max.."']"] ~= nil then    local mystaminamaxiumum = _G["g_UserGlobal['"..user_defined_global_max.."']"]    if mystaminamaxiumum > 0 then     local user_defined_global_current = "MyStamina"     if user_defined_global_current > "" then       local mystamina = 0      if _G["g_UserGlobal['"..user_defined_global_current.."']"] ~= nil then mystamina = _G["g_UserGlobal['"..user_defined_global_current.."']"] end      if g_PlrKeyA == 1 or g_PlrKeyW == 1 or g_PlrKeyS == 1 or g_PlrKeyD == 1 then 	   mystamina = mystamina - 0.25	   if mystamina < 0 then mystamina = 0 end	   _G["g_UserGlobal['"..user_defined_global_current.."']"] = mystamina	   local staminaratio = mystamina / mystaminamaxiumum	   baserunningspeed = baserunningspeed - (1.0-staminaratio)	   if baserunningspeed < 1.0 then baserunningspeed = 1.0 end      end     end		     end   end   end end return baserunningspeedend function gameplayerspeed.drain(amount) if g_UserGlobal ~= nil then  local user_defined_global_max = "MyStaminaMaximum"  if user_defined_global_max > "" then    if _G["g_UserGlobal['"..user_defined_global_max.."']"] ~= nil then    local mystaminamaxiumum = _G["g_UserGlobal['"..user_defined_global_max.."']"]    if mystaminamaxiumum > 0 then     local user_defined_global_current = "MyStamina"     if user_defined_global_current > "" then       local mystamina = 0      if _G["g_UserGlobal['"..user_defined_global_current.."']"] ~= nil then mystamina = _G["g_UserGlobal['"..user_defined_global_current.."']"] end	  mystamina = mystamina - amount	  if mystamina < 0 then mystamina = 0 end	  _G["g_UserGlobal['"..user_defined_global_current.."']"] = mystamina     end		     end   end   end endend function gameplayerspeed.recover()  if g_UserGlobal ~= nil then   local user_defined_global_max = "MyStaminaMaximum"   if user_defined_global_max > "" then 	if _G["g_UserGlobal['"..user_defined_global_max.."']"] ~= nil then	 local mystaminamaxiumum = _G["g_UserGlobal['"..user_defined_global_max.."']"]	 if mystaminamaxiumum > 0 then	  local user_defined_global_current = "MyStamina"	  if user_defined_global_current > "" then 	   local mystamina = 0	   if _G["g_UserGlobal['"..user_defined_global_current.."']"] ~= nil then mystamina = _G["g_UserGlobal['"..user_defined_global_current.."']"] end	   mystamina = mystamina + 0.25	   if mystamina > mystaminamaxiumum then mystamina = mystaminamaxiumum end	   _G["g_UserGlobal['"..user_defined_global_current.."']"] = mystamina	  end	 end		 	end   end   end			  endfunction gameplayerspeed.main()endreturn gameplayerspeed